import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import AuthService from '../services/AuthService';
import { Mail, Lock, Eye, EyeOff, Fingerprint } from 'lucide-react';

function SignIn() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [loading, setLoading] = useState(false);
  const [particles, setParticles] = useState([]);
  const navigate = useNavigate();
  const canvasRef = useRef(null);
  const containerRef = useRef(null);

  // Advanced particle system with canvas rendering
  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    const particleCount = 80;
    let animationFrameId;
    let mouseX = 0;
    let mouseY = 0;

    // Set canvas size
    const resizeCanvas = () => {
      if (containerRef.current) {
        canvas.width = containerRef.current.offsetWidth;
        canvas.height = containerRef.current.offsetHeight;
      }
    };

    resizeCanvas();
    window.addEventListener('resize', resizeCanvas);

    // Create initial particles
    const createParticles = () => {
      const newParticles = Array.from({ length: particleCount }, () => ({
        x: Math.random() * canvas.width,
        y: Math.random() * canvas.height,
        size: Math.random() * 4 + 1,
        speedX: Math.random() * 0.5 - 0.25,
        speedY: Math.random() * 0.5 - 0.25,
        opacity: Math.random() * 0.6 + 0.2,
        color: [
          `rgba(59, 130, 246, ${Math.random() * 0.6 + 0.2})`, // Blue
          `rgba(96, 165, 250, ${Math.random() * 0.6 + 0.2})`, // Light blue
          `rgba(147, 197, 253, ${Math.random() * 0.6 + 0.2})`, // Lighter blue
        ][Math.floor(Math.random() * 3)],
      }));
      return newParticles;
    };

    const particles = createParticles();

    // Track mouse movement for interactive effects
    const handleMouseMove = (e) => {
      const rect = canvas.getBoundingClientRect();
      mouseX = e.clientX - rect.left;
      mouseY = e.clientY - rect.top;
    };

    document.addEventListener('mousemove', handleMouseMove);

    // Animate particles
    const animate = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      
      // Draw connection lines between nearby particles
      ctx.strokeStyle = 'rgba(59, 130, 246, 0.05)';
      ctx.lineWidth = 0.5;
      for (let i = 0; i < particles.length; i++) {
        for (let j = i + 1; j < particles.length; j++) {
          const dx = particles[i].x - particles[j].x;
          const dy = particles[i].y - particles[j].y;
          const distance = Math.sqrt(dx * dx + dy * dy);
          
          if (distance < 100) {
            ctx.beginPath();
            ctx.moveTo(particles[i].x, particles[i].y);
            ctx.lineTo(particles[j].x, particles[j].y);
            ctx.stroke();
          }
        }
      }
      
      // Update and draw particles
      particles.forEach((particle, index) => {
        // Interactive effect: particles slowly move toward mouse
        const dx = mouseX - particle.x;
        const dy = mouseY - particle.y;
        const distance = Math.sqrt(dx * dx + dy * dy);
        
        if (distance < 200) {
          particle.x += dx * 0.002;
          particle.y += dy * 0.002;
        }
        
        // Normal movement
        particle.x += particle.speedX;
        particle.y += particle.speedY;
        
        // Bounce off edges
        if (particle.x < 0 || particle.x > canvas.width) particle.speedX *= -1;
        if (particle.y < 0 || particle.y > canvas.height) particle.speedY *= -1;
        
        // Draw particle
        ctx.beginPath();
        ctx.arc(particle.x, particle.y, particle.size, 0, Math.PI * 2);
        ctx.fillStyle = particle.color;
        ctx.fill();
        
        // Occasionally change particle properties for dynamic effect
        if (Math.random() < 0.01) {
          particle.speedX = Math.random() * 0.5 - 0.25;
          particle.speedY = Math.random() * 0.5 - 0.25;
        }
      });
      
      animationFrameId = requestAnimationFrame(animate);
    };
    
    animate();
    
    // Clean up
    return () => {
      window.removeEventListener('resize', resizeCanvas);
      document.removeEventListener('mousemove', handleMouseMove);
      cancelAnimationFrame(animationFrameId);
    };
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!email || !password) {
      setError('Email and password are required');
      return;
    }
    
    setLoading(true);
    
    // Simulate loading with delay
    setTimeout(() => {
      const success = AuthService.login(email, password);
      setLoading(false);
      
      if (success) {
        // Success animation before navigation
        const form = document.getElementById('signin-form');
        form.classList.add('success-animation');
        setTimeout(() => navigate('/dashboard'), 800);
      } else {
        setError('Invalid email or password');
        // Enhanced shake effect on error
        const form = document.getElementById('signin-form');
        form.classList.add('shake-animation');
        setTimeout(() => form.classList.remove('shake-animation'), 500);
      }
    }, 1000);
  };

  const handleBiometricSignIn = () => {
    setError('Biometric authentication initiated. This is a placeholder for real implementation.');
  };

  const handleSocialSignIn = (provider) => {
    setError(`Sign in with ${provider} initiated. This is a placeholder for real implementation.`);
  };

  return (
    <div 
      ref={containerRef}
      className="flex min-h-screen items-center justify-center bg-gray-900 text-white relative overflow-hidden"
    >
      {/* Canvas-based Particle Effect */}
      <canvas 
        ref={canvasRef} 
        className="absolute inset-0 z-0"
      />

      {/* Dark Gradient Background */}
      <div 
        className="absolute inset-0 bg-gradient-radial from-gray-800 to-gray-900 opacity-80"
      ></div>

      {/* Glowing orbs in background */}
      <div className="absolute top-1/4 left-1/4 w-32 h-32 rounded-full bg-blue-600/20 blur-3xl animate-pulse-slow"></div>
      <div className="absolute bottom-1/3 right-1/4 w-40 h-40 rounded-full bg-indigo-600/20 blur-3xl animate-pulse-slow animation-delay-1000"></div>
      <div className="absolute top-2/3 left-1/3 w-24 h-24 rounded-full bg-sky-600/20 blur-3xl animate-pulse-slow animation-delay-2000"></div>

      {/* Light beams effect */}
      <div className="absolute inset-0 overflow-hidden opacity-20">
        <div className="light-beam light-beam-1"></div>
        <div className="light-beam light-beam-2"></div>
        <div className="light-beam light-beam-3"></div>
      </div>

      {/* Sign-In Container with enhanced glass morphism */}
      <div className="w-full max-w-sm p-8 bg-gray-800/40 rounded-lg backdrop-blur-xl relative z-10 transform hover:scale-102 transition-all duration-500 border border-white/10 shadow-glow">
        {/* Animated highlight border */}
        <div className="absolute inset-0 border border-blue-400/30 rounded-lg glow-border"></div>
        
        <div className="flex justify-center mb-6 animate-fadeIn">
          {/* Enhanced Logo with glow effect */}
          <div className="relative">
            <div className="absolute inset-0 rounded-full bg-blue-500/50 blur-md animate-ping-slow"></div>
            <div className="absolute inset-0 rounded-full bg-blue-400/30 blur-sm animate-pulse"></div>
            <img 
              src="https://bilvantis.io/wp-content/uploads/2022/12/Bilvantis-logo-png.jpg"
              alt="Bilvantis Logo"
              className="h-14 rounded relative z-10 animate-float"
            />
          </div>
        </div>
        
        {/* Holographic Text Effect */}
        <h2 className="text-xl font-bold text-center mb-6 holographic-text">Sign In</h2>
        
        {error && (
          <div className="bg-red-900/30 text-red-200 p-3 rounded mb-4 text-sm animate-fadeIn border-l-4 border-red-500 backdrop-blur-sm">
            <div className="error-icon-pulse"></div>
            {error}
          </div>
        )}
        
        <form id="signin-form" onSubmit={handleSubmit} className="form-glow">
          <div className="mb-4 transform hover:translate-x-1 transition-transform input-container">
            <label className="block text-gray-300 mb-2 text-sm" htmlFor="email">
              <div className="flex items-center">
                <Mail size={14} className="mr-2 text-blue-400 animate-pulse-subtle" />
                <span>Email</span>
              </div>
            </label>
            <input
              id="email"
              type="email"
              className="w-full px-3 py-2 border rounded-lg bg-gray-700/30 backdrop-blur-md border-gray-600/50 text-white focus:ring-2 focus:ring-blue-500/50 focus:outline-none focus:border-transparent transition-all text-sm glow-input"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="your@email.com"
            />
            <div className="input-focus-effect"></div>
          </div>
          
          <div className="mb-6 transform hover:translate-x-1 transition-transform input-container">
            <label className="block text-gray-300 mb-2 text-sm" htmlFor="password">
              <div className="flex items-center">
                <Lock size={14} className="mr-2 text-blue-400 animate-pulse-subtle" />
                <span>Password</span>
              </div>
            </label>
            <div className="relative">
              <input
                id="password"
                type={showPassword ? "text" : "password"}
                className="w-full px-3 py-2 border rounded-lg bg-gray-700/30 backdrop-blur-md border-gray-600/50 text-white focus:ring-2 focus:ring-blue-500/50 focus:outline-none focus:border-transparent transition-all text-sm glow-input"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="••••••••"
              />
              <button 
                type="button" 
                className="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-400 hover:text-white transition-colors"
                onClick={() => setShowPassword(!showPassword)}
              >
                {showPassword ? <EyeOff size={16} /> : <Eye size={16} />}
              </button>
              <div className="input-focus-effect"></div>
            </div>
          </div>
          
          {/* Enhanced Sign In Button with layered effects */}
          <button
            type="submit"
            disabled={loading}
            className="w-full py-2 rounded-lg font-bold text-sm relative overflow-hidden group signin-button"
          >
            <div className="absolute inset-0 bg-gradient-to-r from-blue-600 to-indigo-600 rounded-lg"></div>
            <div className="absolute inset-0 bg-gradient-to-r from-blue-400 to-indigo-500 rounded-lg opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>
            <div className="absolute inset-0 shine-effect"></div>
            <span className="relative z-10 flex items-center justify-center">
              {loading ? (
                <span className="flex items-center justify-center">
                  <div className="spinner mr-2"></div>
                  Signing In...
                </span>
              ) : "Sign In"}
            </span>
            <span className="absolute bottom-0 left-0 w-full h-1 bg-white/30 transform translate-y-1 group-hover:translate-y-0 transition-transform duration-300"></span>
          </button>
        </form>
        
        {/* Biometric Option with enhanced effect */}
        <div className="mt-6 mb-6">
          <button 
            onClick={handleBiometricSignIn}
            className="w-full flex items-center justify-center py-2 px-4 border border-gray-600/50 rounded-lg hover:bg-gray-700/30 transition-all text-sm group biometric-button"
          >
            <div className="fingerprint-scan"></div>
            <Fingerprint size={16} className="mr-2 text-blue-400 group-hover:text-blue-300" />
            <span className="group-hover:text-blue-300 transition-colors">Sign In with Biometrics</span>
          </button>
        </div>
        
        {/* Social Sign In Options with enhanced hover effects */}
        <div className="mt-4 mb-6">
          <p className="text-center text-gray-400 mb-3 text-sm">Or sign in with</p>
          <div className="grid grid-cols-3 gap-3">
            {/* Social media buttons with enhanced effects */}
            <button 
              onClick={() => handleSocialSignIn('Google')}
              className="flex items-center justify-center py-2 px-3 border border-gray-600/50 rounded-lg hover:bg-gray-700/30 transition-all text-sm social-button"
            >
              <svg className="w-4 h-4 mr-1" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                <path d="M12.48 10.92v3.28h7.84c-.24 1.84-.853 3.187-1.787 4.133-1.147 1.147-2.933 2.4-6.053 2.4-4.827 0-8.6-3.893-8.6-8.72s3.773-8.72 8.6-8.72c2.6 0 4.507 1.027 5.907 2.347l2.307-2.307C18.747 1.44 16.133 0 12.48 0 5.867 0 .307 5.387.307 12s5.56 12 12.173 12c3.573 0 6.267-1.173 8.373-3.36 2.16-2.16 2.84-5.213 2.84-7.667 0-.76-.053-1.467-.173-2.053H12.48z" fill="#FFF"/>
              </svg>
              Google
            </button>
            
            <button 
              onClick={() => handleSocialSignIn('Facebook')}
